package page.danya.models;import org.hibernate.search.annotations.Field;import org.hibernate.search.annotations.Indexed;import javax.persistence.*;import java.util.Collection;import java.util.List;@Entity@Table(name = "grp")@Indexedpublic class Group {    @Field    private int memory;    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private int id;    private String name;    @OneToMany(mappedBy = "group")    private List<APP_User> user;    @OneToMany(mappedBy = "group")    private List<Teaching> teaching;    public Group() {    }    public Group(int id, String name, List<APP_User> user) {        this.id = id;        this.name = name;        this.user = user;    }    public Group(int id) {        this.id = id;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public List<APP_User> getUser() {        return user;    }    public void setUser(List<APP_User> user) {        this.user = user;    }}